Dungeon
mirkwood.ogg









 .HTML\_Audio\_player { z-index:10; 
 background: linear-gradient(135deg,#2a2a2b 0,#2a2a2b 49%,#2a2a2b 49%,#444547 100%); 
 color: inherit; 
 min-height:120px; max-height:120px; 
 display:flex;
 box-shadow: rgba(0, 0, 0, 0.16) 0px 3px 6px, rgba(0, 0, 0, 0.23) 0px 3px 6px; 
 flex-direction:row;padding: 20px 10px 20px;} 

 .Audio\_Player\_image { width:170px; display: flex; justify-content: center; } 
 .player-content { 
 flex-grow:1; 
 display:flex; 
 flex-direction:column;}

 .player-info { 
 flex-grow:1; display:flex; 
 flex-direction:column; 
 justify-content:center; 
 padding-left:10px;} 

 .song-name { font-size:18px; font-weight:600; } .k2\_audio\_player { display:flex;} 

 audio { flex-grow:1; height:60px; } 

 audio::-webkit-media-controls-play-button {
 background-color: #cccccc;
 border-radius: 50%;}

 audio::-webkit-media-controls-play-button:hover {
 background-color: rgba(177,212,224, .7);}

 audio::-webkit-media-controls-panel {
 background: #e9e8f2;}

 .Audio\_Player\_image img:hover{animation:rotating 3s linear infinite} @keyframes rotating{from{transform:rotate(0deg)}to{transform:rotate(360deg)}}





